mapper.select:
select , entrust.name AS entrustName, er.`case_source_name` AS sourceName FROM case_batch LEFT JOIN entrust ON case_batch.entrust_id = entrust.id LEFT JOIN entrust_case_Product_releation er ON case_batch.entrust_product_id = er.product_id WHERE 1=1 AND STATUS != -1
and case_batch.id in ${params.batchCode}
and case_batch.entrust_id = #{params.entrustId}
and collection_id like CONCAT('%','${params.collectionId}','%' )
and case_batch.state like CONCAT('%','${params.state}','%' )
and type_id = #{params.typeId}
and begin_date BETWEEN '${params.beginDate}' AND'${params.beginDate1}'
order by ${sort} ${order}
order by create_time desc

mapper.update:
update
case_batch
set
status=#{status}
where id=#{id}
