mapper.select:
SELECT cp.*, employee_info.user_name from case_paid cp LEFT JOIN
employee_info on cp.se_no=employee_info.id
WHERE 1=1


	and cp.state in

		#{params.state[${index}]}




	and cp.case_id='${params.caseId}'


	and cp.repay_type='${params.repayType}'





		order by ${sort}
		${order}


		order by cp.create_time desc
