mapper.select:
SELECT tj.name, tj.emId, tj.orgId, tj.state, tj.case_count, tj.new_case_count, tj.lose_case_count, tj.involve_case_count, tj.negotiation_case_count, tj.family_case_count, tj.someone_case_count, tj.oneself_case_count, #暂停催收 25 要求退案 26 承诺 31 疑似欺诈 32 tj.stop_case_count, tj.back_case_count, tj.promise_case_count, tj.cheat_case_count FROM ( #案件数量 新案数量0 失联数量87 30 33 涉案数量34 谈判数量88 中家人数量28 中第三人数量29 中本人数量27 SELECT em.user_name AS name,em.id AS emId,em.`status` AS state, em.org_id as orgId, case_info.case_backdate,case_info.`case_date`, case_info.`case_assigned_id`, COUNT(DISTINCT case_info.id) AS case_count, SUM(CASE WHEN (case_info.collec_state_id = '0' ) THEN 1 ELSE 0 END) AS new_case_count, SUM(CASE WHEN (case_info.collec_state_id = '30' OR case_info.collec_state_id = '87' OR case_info.collec_state_id = '33') THEN 1 ELSE 0 END) AS lose_case_count, SUM(CASE WHEN case_info.collec_state_id = '34' THEN 1 ELSE 0 END) AS involve_case_count, SUM(CASE WHEN case_info.collec_state_id = '88' THEN 1 ELSE 0 END) AS negotiation_case_count, SUM(CASE WHEN case_info.collec_state_id = '28' THEN 1 ELSE 0 END) AS family_case_count, SUM(CASE WHEN case_info.collec_state_id = '29' THEN 1 ELSE 0 END) AS someone_case_count, SUM(CASE WHEN case_info.collec_state_id = '27' THEN 1 ELSE 0 END) AS oneself_case_count, #暂停催收 25 要求退案 26 承诺 31 疑似欺诈 32 SUM(CASE WHEN case_info.collec_state_id = '25' THEN 1 ELSE 0 END) AS stop_case_count, SUM(CASE WHEN case_info.collec_state_id = '26' THEN 1 ELSE 0 END) AS back_case_count, SUM(CASE WHEN case_info.collec_state_id = '31' THEN 1 ELSE 0 END) AS promise_case_count, SUM(CASE WHEN case_info.collec_state_id = '32' THEN 1 ELSE 0 END) AS cheat_case_count FROM `employee_info` em LEFT JOIN case_info ON em.id=case_info.`case_assigned_id`
WHERE 1=1 and case_info.status!=-1 #AND case_info.org_id IS NOT NULL #AND (case_date <='2016-07-31' AND case_info.case_backdate >= '2016-07-01')
and (case_date <= '${params.normalDate1}' and case_info.case_backdate >= '${params.normalDate}')
GROUP BY case_assigned_id
) AS tj WHERE 1=1
and tj.emId in
#{params.emIds[${index}]}
and orgId IN ( SELECT id FROM sys_organization WHERE path LIKE CONCAT(( SELECT path FROM sys_organization WHERE id = #{params.orgId} ), '%') )
order by ${sort} ${order}
ORDER BY tj.state DESC, CONVERT(tj.name USING gbk) ASC
