mapper.select:
select

from

where parent_id = #{parentId}

	and type = #{type}


where UserR.user_id = #{userId}

	and R.type = #{type}

and R.parent_id is null
and status = '${@com.atguigu.commons.CoreConst@STATUS_NORMAL}'
union 

where UserR.user_id = #{userId}

	and R.type = #{type}

and R.parent_id is null
and status = '${@com.atguigu.commons.CoreConst@STATUS_NORMAL}'


where UserR.user_id = #{userId} 

	and R.type = #{type}

and R.parent_id = #{parentId}
and status = '${@com.atguigu.commons.CoreConst@STATUS_NORMAL}'
union 

where UserR.user_id = #{userId}

	and R.type = #{type}

and R.parent_id = #{parentId}
and status = '${@com.atguigu.commons.CoreConst@STATUS_NORMAL}'


where UserR.user_id = #{userId}
and status = '${@com.atguigu.commons.CoreConst@STATUS_NORMAL}'
union 

where UserR.user_id = #{userId}
and status = '${@com.atguigu.commons.CoreConst@STATUS_NORMAL}'
